import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { taskStore } from "../../stores/TaskStore";
import { userStore } from "../../stores/UserStore";
import { categoryStore } from "../../stores/CategoryStore";
import "../EditTask/EditTask.css";

function EditTask() {
    const navigate = useNavigate();
    const { categories } = categoryStore(); // Obtém a lista de categorias
    const { id } = useParams(); // Obtém o ID da tarefa a partir dos parâmetros de rota
    const token = userStore((state) => state.token);
    const task = taskStore((state) => state.task); // Obtém os detalhes da tarefa da taskStore

    const [taskDetails, setTaskDetails] = useState({
        title: task?.title || "",
        description: task?.description || "",
        priority: task?.priority || "",
        startDate: task?.startDate || "",
        limitDate: task?.limitDate || "",
        category: task?.category ? { name: task.category.name } : { name: "" },
    });
    
    
    const [status, setStatus] = useState(""); // Estado para o status da tarefa
    const [priority, setPriority] = useState(""); // Estado para a prioridade da tarefa
   

    useEffect(() => {
        if (task) {
            setTaskDetails({
                title: task.title || "",
                description: task.description || "",
                priority: task.priority || "",
                startDate: task.startDate || "",
                limitDate: task.limitDate || "",
                category: task.category ? { name: task.category.name } : { name: "" },
            });
        }
    }, [task]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        if (name === "category") {
            // Crie o objeto de categoria com o atributo name
            const category = { name: value };
            setTaskDetails({ ...taskDetails, category: category });
        } else {
            setTaskDetails({ ...taskDetails, [name]: value });
        }
    };
    

    const handleDateChange = (event) => {
        const { name, value } = event.target;
        if (name === "startDate" && taskDetails.limitDate && value > taskDetails.limitDate) {
            // Se a nova data de início for posterior à data limite, atualize a data limite também
            setTaskDetails({
                ...taskDetails,
                startDate: value,
                limitDate: value // Define a data limite como a nova data de início
            });
        } else {
            setTaskDetails({ ...taskDetails, [name]: value });
        }
    };
    

    const handleSaveTask = async () => {
        try {
            // Atualiza a tarefa no backend usando o endpoint correto
            const response = await fetch(`http://localhost:8080/project_backend/rest/users/updatetask/${id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    token: token,
                },
                body: JSON.stringify(taskDetails),
            });

            if (response.ok) {
                // Se a atualização for bem-sucedida, navega de volta para a página inicial
                navigate("/home", { replace: true });
            } else {
                const responseBody = await response.text();
                console.error("Error updating task:", response.statusText, responseBody);
            }
        } catch (error) {
            console.error("Error updating task:", error);
        }
    };


        // Função para lidar com a alteração do status da tarefa
        const handleStatusClick = (status) => {
            setStatus(status);
        };
    
        // Função para lidar com a alteração da prioridade da tarefa
        const handlePriorityClick = (priority) => {
            setPriority(priority);
        };

    return (
        <div className="edit-task">
            <div className="editTask-title">
                <div className="labels-editTask-top">
                    <label htmlFor="titulo-task">Title</label>
                </div>
                <div className="input-editTask-title">
                    <input type="text" id="titulo-task" name="title" value={taskDetails.title} onChange={handleInputChange} />
                </div>
            </div>
            <div className="editTask-description">
                <div className="labels-editTask-top">
                    <label htmlFor="descricao-task">Description</label>
                </div>
                <div className="input-editTask-description">
                    <textarea id="descricao-task" name="description" value={taskDetails.description} onChange={handleInputChange} />
                </div>
            </div>
            <div className="task-status">
                        <div className="field-titles">
                            <h4 className="taskH4" id="status-h4">Status</h4>
                        </div>
                        <div className="status-buttons">
                            <button className={`status-button todo ${statusId === "100" ? "selected" : ""}`} id="todo-button" onClick={() => handleStatusClick("100")} >To Do</button>
                            <button className={`status-button doing ${status === "200" ? "selected" : ""}`} id="doing-button" onClick={() => handleStatusClick("200")} >Doing</button>
                            <button className={`status-button done ${status === "300" ? "selected" : ""}`} id="done-button" onClick={() => handleStatusClick("300")} >Done</button>
                        </div>
            </div>
            <div className="task-priority">
                        <div className="field-titles">
                            <h4 className="taskH4" id="priority-h4">Priority</h4>
                        </div>
                        <div className="priority-buttons">
                            <button className={`priority-button low ${priority === "100" ? "selected" : ""}`} id="low-button" onClick={() => handlePriorityClick("100")} >Low</button>
                            <button className={`priority-button medium ${priority === "200" ? "selected" : ""}`} id="medium-button" onClick={() => handlePriorityClick("200")} >Medium</button>
                            <button className={`priority-button high ${priority === "300" ? "selected" : ""}`} id="high-button" onClick={() => handlePriorityClick("300")} >High</button>
                        </div>
            </div>
            <div className="editTask-startDate">
                <div className="labels-editTask-top">
                    <label htmlFor="dataInicio-task">Start Date</label>
                </div>
                <div className="input-editTask-startDate">
                    <input type="date" id="dataInicio-task" name="startDate" value={taskDetails.startDate} onChange={handleDateChange} />
                </div>
            </div>
            <div className="editTask-limitDate">
                <div className="labels-editTask-top">
                    <label htmlFor="dataLimite-task">Limit Date</label>
                </div>
                <div className="input-editTask-limitDate">
                    <input type="date" id="dataLimite-task" name="limitDate" value={taskDetails.limitDate} onChange={handleDateChange} />
                </div>
            </div>
            <div className="editTask-category">
                <div className="labels-editTask-top">
                    <label htmlFor="categoria-task">Category</label>
                </div>
                <div className="input-editTask-category">
                    <select id="categoria-task" name="category" value={taskDetails.category} onChange={handleInputChange}>
                        <option value="">Choose category</option>
                        {categories && categories.map(category => (
                            <option key={category} value={category}>{category}</option>
                        ))}
                    </select>
                </div>
            </div>
            <button className="save-button" onClick={handleSaveTask}>Save</button>
        </div>
    );
}

export default EditTask;
