import React from "react";
//import Sidebar from "../components/navbar/sidebar";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import '../index.css'
import { userStore } from "../stores/UserStore.jsx";

function Register() {
    const [inputs, setInputs] = useState({});
    const navigate = useNavigate();
    const updateName = userStore(state => state.updateName);

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
    
        setInputs(values => ({...values, [name]: value}));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
        navigate('/home', {replace: true});
        updateName(inputs.username);
    }

    return (
        <div className="Register" id="register-outer-container">
        <div className="page-wrap" id="register-page-wrap">
        <div className="center-container-register">
            <div className="registerPanel">
                <img id="logo-register" src="multimedia/logo-scrum-01.png" width="250" />
                <form id="registrationForm" className="inputs-register">
                    <div className="right-inputs">
                        <input type="text" class="inputRegister-fields" id="username-register" name="username" placeholder="Username" required />
                        <input type="password" class="inputRegister-fields" id="password-register" name="password" placeholder="Password" required />
                        <input type="password" class="inputRegister-fields" id="passwordConfirm-register" name="passwordConfirm" placeholder="Confirm Password" required />
                        <input type="email" class="inputRegister-fields" id="email-register" name="email" placeholder="Email" required />
                    </div>
                    <div class="left-inputs">
                        <input type="text" class="inputRegister-fields" id="firstName-register" name="firstName" placeholder="First Name" required />
                        <input type="text" class="inputRegister-fields" id="lastName-register" name="lastName" placeholder="Last Name" required />
                        <input type="text" class="inputRegister-fields" id="phone-register" name="phone" placeholder="Phone" required />
                        <input type="url" class="inputRegister-fields" id="photoURL-register" name="photoURL" placeholder="Photo URL" required />
                    </div>
                    <div class="submitButton">
                        <button type="submit" id="registerButton-register">Register</button>
                    </div>
                </form>
                <a id="backToLoginLink" href="index.html">Login</a>
                <p id="warningMessage4"></p>
            </div>
        </div>
        </div>
        </div>
    );
}

export default Register;
