import React from 'react';
import "../TaskCard/TaskCard.css";
import { Link } from 'react-router-dom';

export default function TaskCard({ task }) {
    const { id, title, description, priority, erased } = task;

    // Traduzindo a prioridade de int para string
    const translatePriority = (priorityInt) => {
        switch (priorityInt) {
            case 100:
                return "Low";
            case 200:
                return "Medium";
            case 300:
                return "High";
            default:
                return "";
        }
    }

    const priorityString = translatePriority(priority);

    const getPriorityBorderClass = () => {
        switch (priorityString) {
            case "High":
                return "border-red";
            case "Medium":
                return "border-yellow";
            case "Low":
                return "border-green";
            default:
                return "";
        }
    }

    const priorityBorderClass = getPriorityBorderClass();

    const handleEraseTask = async () => {
        // Implemente a lógica para apagar a tarefa aqui
    };

    const handleDeleteTask = async () => {
        // Implemente a lógica para excluir definitivamente a tarefa aqui
    };

    const handleRestoreTask = async () => {
        // Implemente a lógica para restaurar a tarefa aqui
    };

    return (
        <div className={`task ${priorityBorderClass}`}>
            <Link to={`/edit-task/${id}`} className="task-title-solo">
                {title}
            </Link>
            <div className="task-description-solo">{description}</div>
            {erased ? (
                <div className="task-icons">
                    <img src=".." alt="Restore" className="restore-icon" onClick={handleRestoreTask} />
                </div>
            ) : (
                <div className="task-icons">
                    <img src={deleteIcon} alt="Delete" className="delete-icon" onClick={handleDeleteTask} />
                    <img src={eraseIcon} alt="Erase" className="erase-icon" onClick={handleEraseTask} />
                </div>
            )}
        </div>
    );
}
