    return (
        <div className={`user ${typeOfUserBorderClass}`} style={{ backgroundColor: visible ? "white" : "#EDEDED" }}>
            {visible ? (
                <Link to={`/edit-user/${id}`} className="user-username-solo">
                    {firstName} {lastName}
