import React, { useState } from "react";
import "../Profile/Profile.css";
import { userStore } from "../../stores/UserStore";

function EditProfile() {
    const username = userStore(state => state.username);
    const email = userStore(state => state.email);
    const firstName = userStore(state => state.firstName);
    const lastName = userStore(state => state.lastName);
    const phone = userStore(state => state.phone);
    const photoURL = userStore(state => state.photoURL);

    const updateEmail = userStore(state => state.updateEmail);
    const updateFirstName = userStore(state => state.updateFirstName);
    const updateLastName = userStore(state => state.updateLastName);
    const updatePhone = userStore(state => state.updatePhone);
    const updatePhotoURL = userStore(state => state.updatePhotoURL);


  return (
    <div>
      <div className="editProfilePanel">
      <label id="username-title-editProfile">{ username }</label>
            <form class="editProfile-register" id="edit-profile-form">
                <div class="editProfile-fieldsContainer">
                    <div class="left-fields-editProfile">
                        <label class="labels-edit-profile" id="email-editProfile-label">Email</label>
                        <input type="email" class="editProfile-fields" id="email-editProfile" name="email" onChange={handleChange} required >{ email }</input>
                        <label class="labels-edit-profile" id="firstName-editProfile-label">First Name</label>
                        <input type="text" class="editProfile-fields" id="firstName-editProfile" name="firstName" onChange={handleChange} required >{ firstName }</input>
                        <label class="labels-edit-profile" id="lastName-editProfile-label">Last Name</label>
                        <input type="text" class="editProfile-fields" id="lastName-editProfile" name="lastName" onChange={handleChange} required >{ lastName }</input>
                        <label class="labels-edit-profile" id="phone-editProfile-label">Phone</label>
                        <input type="text" class="editProfile-fields" id="phone-editProfile" name="phone" onChange={handleChange} required >{ phone }</input>
                        <label class="labels-edit-profile" id="photoURL-editProfile-label">Photo URL</label>
                        <input type="url" class="editProfile-fields" id="photoURL-editProfile" name="photoURL" >{ photoURL }</input>
                    </div>
                </div>
                <div class="editProfile-Buttons">
                        <button type="submit" id="profile-save-button">Save</button>
                        <button type="reset" id="profile-cancel-button">Cancel</button>
                </div>
            </form>
            </div>
    </div>
  );
}

export default EditProfile;
