// Avatar.jsx
import React from 'react';
import './Avatar.css'; // Importa o arquivo CSS

const Avatar = ({ imageUrl = 'https://st3.depositphotos.com/6672868/13701/v/450/depositphotos_137014128-stock-illustration-user-profile-icon.jpg', label }) => {
    return (
        <div className="avatar-container">
            <img
                src={imageUrl}
                alt="Avatar"
                className="avatar-image"
            />
            <span className="avatar-label">{label}</span>
        </div>
    );
};

export default Avatar;
