import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { taskStore } from "../../stores/TaskStore";
import { userStore } from "../../stores/UserStore";
import { categoryStore } from "../../stores/CategoryStore";
import "../EditTask/EditTask.css";

function EditTask() {
    const navigate = useNavigate();
    const { categories } = categoryStore();
    const { taskId } = useParams();
    const token = userStore((state) => state.token);
    const userId = userStore((state) => state.userId); // Obtém o userId do usuário logado
    const task = taskStore((state) => state.tasks.find(task => task.id === taskId));

    const [taskDetails, setTaskDetails] = useState({
        title: "",
        description: "",
        priority: "",
        startDate: "",
        limitDate: "",
        category: null,
    });

    const [selectedCategory, setSelectedCategory] = useState(null);
    const [stateId, setStateId] = useState("");
    const [priority, setPriority] = useState("");

    useEffect(() => {
        if (task) {
            setTaskDetails({
                title: task.title || "",
                description: task.description || "",
                priority: task.priority || "",
                startDate: task.startDate || "",
                limitDate: task.limitDate || "",
                category: task.category || null,
            });
            setSelectedCategory(task.category || null);
            setStateId(task.stateId || "");
            setPriority(task.priority || "");
        }
    }, [task]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;

        if (name === "category") {
            const selectedCategory = categories.find(category => category.name === value) || null;
            setSelectedCategory(selectedCategory);
        } else {
            setTaskDetails({ ...taskDetails, [name]: value });
        }
    };

    const handleDateChange = (event) => {
        const { name, value } = event.target;

        if (name === "startDate" && taskDetails.limitDate && value > taskDetails.limitDate) {
            setTaskDetails({
                ...taskDetails,
                startDate: value,
                limitDate: value
            });
        } else {
            setTaskDetails({ ...taskDetails, [name]: value });
        }
    };

    const handleSaveTask = async () => {
        try {
            // Verifica se o userId associado à tarefa é o mesmo que o userId do usuário logado
            if (task.userId !== userId) {
                // Se não for o mesmo userId, não permite a edição da tarefa
                console.error("Você só pode editar tarefas da sua autoria.");
                return;
            }
            
            const response = await fetch(`http://localhost:8080/project_backend/rest/users/updatetask/${taskId}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    token: token,
                },
                body: JSON.stringify({ ...taskDetails, category: selectedCategory }),
            });

            if (response.ok) {
                navigate("/home", { replace: true });
            } else {
                const responseBody = await response.text();
                console.error("Error updating task:", response.statusText, responseBody);
            }
        } catch (error) {
            console.error("Error updating task:", error);
        }
    };

    const handlestateClick = (stateId) => {
        setStateId(stateId);
    };
    
    const handlePriorityClick = (priority) => {
        setPriority(priority);
    };

    return (
        <div className="edit-task">
            {/* Conteúdo do componente EditTask */}
        </div>
    );
}

export default EditTask;
