import { create } from "zustand";
import { persist, createJSONStorage } from "zustand/middleware";

// Define a store
export const taskStore = create(
    persist(
        (set) => ({
            title: "",
            description: "",
            priority: "",
            startDate: "",
            limitDate: "",

            id: "",
            stateId: "",
            category: "",
            

            updateTitle: (title) => set({ title }),
            updateDescription: (description) => set({ description }),
            updatePriority: (priority) => set({ priority }),
            updateStartDate: (startDate) => set({ startDate }),
            updateLimitDate: (limitDate) => set({ limitDate }),
            



            tasks: [],
            // Função para adicionar uma nova tarefa
            addTask: (task) =>
                set((state) => ({
                    tasks: [...state.tasks, task],
                })),
            // Função para editar uma tarefa existente
            editTask: (oldTask, updatedTask) =>
                set((state) => ({
                    tasks: state.tasks.map((task) =>
                        task.name === oldTask ? { ...task, ...updatedTask } : task
                    ),
                })),
            // Função para excluir uma tarefa
            deleteTask: (deleteTask) =>
                set((state) => ({
                    tasks: state.tasks.filter((task) => task.name !== deleteTask),
                })),
        }),
        {
            name: "taskStore",
            storage: createJSONStorage(() => sessionStorage),
        }
    )
);
