import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../EditTaskAside/EditTaskAside.css";
import { userStore } from "../../stores/UserStore";
import { tasksStore } from "../../stores/TasksStore";

function EditTaskAside() {
    const navigate = useNavigate();
    const { taskId } = useParams();
    const token = userStore((state) => state.token);
    const { fetchTasks } = tasksStore();

    const handleDeleteTask = async () => {
        try {
            const response = await fetch(`http://localhost:8080/project_backend/rest/users/${taskId}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    token: token
                },
            });

            if (response.ok) {
                fetchTasks();
                navigate("/home", { replace: true });
            } else {
                const responseBody = await response.text();
                console.error("Error deleting task:", response.statusText, responseBody);
            }
        } catch (error) {
            console.error("Error deleting task:", error);
        }
    };

    return (
        <div className="aside-edit-task">
            <div className="aside-delete">
                <h2>Delete Task</h2>
            </div>
            <div className="aside-description">
                <button className="delete-button" id="delete-button" onClick={handleDeleteTask}>Delete</button>
            </div>
        </div>
    );
}

export default EditTaskAside;
